mapper.select:
SELECT * FROM v_navigation
WHERE parent_id = #{parentId}
ORDER BY sequence DESC

SELECT * FROM v_navigation WHERE 1=1

    AND parent_id IS NULL


    AND parent_id = #{parentId}


    AND status= #{status}


    AND version= #{version}

ORDER BY sequence DESC

SELECT * FROM v_navigation WHERE parent_id IS NULL

    AND status= #{status}


    AND version= #{version}

ORDER BY sequence DESC

SELECT * FROM v_navigation WHERE status in (0,1)
AND version= #{version}
ORDER BY sequence DESC

mapper.update:
UPDATE v_navigation
SET sequence =
#{sequence}
WHERE id =
#{id}
