mapper.select:
SELECT * FROM
log_requisition WHERE requisition_id=#{requisitionId}
ORDER BY
create_date DESC
LIMIT #{pager.startNumber},#{pager.pageSize}

SELECT count(id)
FROM
log_requisition WHERE requisition_id=#{requisitionId}

mapper.update:
UPDATE log_requisition SET requisition_sn=#{requisitionSn} WHERE requisition_sn is null AND requisition_item_id
IN

    #{id}
