mapper.select:
SELECT d.* FROM m_share d,m_member_follow r WHERE r.from_id=#{memberId} and d.member_id=r.to_id
AND d.status=#{status}
ORDER BY d.top DESC,d.create_date DESC LIMIT #{pager.startNumber},#{pager.pageSize}

SELECT count(m.id)
FROM m_share m, m_share_tag_relation r where m.id=r.share_id
and tag_id=#{tagId}

SELECT
COALESCE(SUM(likes),0) FROM m_share WHERE
member_id=#{memberId}

    AND status=#{status}

mapper.update:
UPDATE m_share
SET
url=#{url} WHERE id=#{id}

UPDATE m_share

    top=#{status},modify_date=now()

WHERE id=#{id}

UPDATE m_share SET modify_date=now(),
designer_id=#{designerId}

    ,role=#{role}

WHERE id=#{id}

UPDATE m_share
SET modify_date=now()

    ,product_id=#{productId}


    ,designer_id=#{designerId}

WHERE id=#{id}

UPDATE m_share SET
likes=likes+#{likes} WHERE
id=#{id}

UPDATE m_share SET
comments=comments+#{comments}
WHERE
id=#{id}

UPDATE m_share
SET modify_date=now(), product_id=0
WHERE id=#{id}

UPDATE m_share SET
status=1,verify_date=now(),verify_man=#{verifyMan} WHERE
id=#{id}

UPDATE m_share SET
status=0,cancel_date=now(),cancel_man=#{cancelMan} WHERE
id=#{id}

UPDATE m_share SET

    head_pic=#{headPic},

nickname=#{nickName},modify_date=NOW() WHERE member_id=#{memberInfoId}
