SELECT t.*, d.operation FROM ( SELECT oi.product_id AS productId, oi.product_sn AS productSn, oi.product_sku_sn AS productSku, oi.designer_id AS designerId, oi.designer_code AS designerCode, oi.designer_name AS designerName, SUM(oi.product_quantity) AS quantity, o.payment_time AS payTime, oi.product_name AS productName, product_img AS productImg FROM orderItem oi, `orders` o WHERE 1=1
AND ( (oi.payment_type!=null AND oi.payment_type!=3 AND oi.delivery_time > #{startDayTime} AND oi.delivery_time < #{endDayTime}) OR (oi.payment_type=3 AND oi.delivery_time > #{startDayTime} AND oi.delivery_time < #{endDayTime}) )
AND oi.delivery_time > #{startDayTime} AND oi.delivery_time < #{endDayTime}
AND oi.item_close_time > #{startDayTime} AND oi.item_close_time < #{endDayTime}
AND o.order_status IN (2, 3, 4, 8, -2, -4) AND oi.order_id=o.id GROUP BY oi.product_sku_id LIMIT #{pager.startNumber},#{pager.pageSize})t , p_brand d WHERE d.id=t.designerId

SELECT COUNT(product_sku_id) FROM (
SELECT DISTINCT oi.product_sku_id
FROM orderItem oi, `orders` o
WHERE oi.order_id=o.id


        AND (
        (oi.payment_type!=null AND oi.payment_type!=3
        AND oi.delivery_time > #{startDayTime}
