SELECT oi.product_sku_id AS productSkuId, oi.product_sku_sn AS productSkuSn, oi.product_img AS productImg, oi.product_id AS productId, oi.designer_code AS designerCode, oi.designer_name AS designerName, oi.product_sn AS productSn, oi.product_name AS productName, SUM(oi.product_price*oi.product_quantity-oi.promotion_amount-oi.order_promotion_amount-oi.red_amount-oi.coupon_amount) AS totalAmount, SUM(oi.product_quantity) AS totalCount, s.pop_store AS popStore, s.store AS store, d.operation AS operation FROM orderItem oi,p_product_sku s,p_brand d WHERE oi.product_sku_id=s.id AND oi.designer_id=d.id AND oi.status NOT IN('MALLCLOSE', 'AFTERCLOSE', 'CLOSE', 'INIT')
GROUP BY oi.product_sku_id ORDER BY
${searcher.orderStr}
LIMIT #{pager.startNumber},#{pager.pageSize}

SELECT oi.designer_name AS designerName, SUM(oi.product_price*oi.product_quantity -oi.promotion_amount-oi.order_promotion_amount-oi.red_amount-oi.coupon_amount) AS totalAmount, SUM(oi.product_quantity) AS totalCount, d.operation AS operation FROM orderItem oi, p_brand d WHERE oi.designer_id=d.id AND oi.status NOT IN('MALLCLOSE', 'AFTERCLOSE', 'CLOSE', 'INIT')
GROUP BY oi.designer_id ORDER BY
${searcher.orderStr}
LIMIT #{pager.startNumber},#{pager.pageSize}

SELECT GROUP_CONCAT(designer_name) FROM (SELECT designer_name,count(*) AS count FROM orderItem WHERE buyer_member_id=#{memberId} AND status='SUCCESS'
AND create_date >#{beginDate}
AND create_date <#{endDate}
GROUP BY designer_name ORDER BY count DESC)t LIMIT 0, #{size}

SELECT *, product_price*product_quantity AS MaxMoney FROM orderItem WHERE buyer_member_id=#{memberId} AND status='SUCCESS'
AND create_date >#{beginDate}
AND create_date <#{endDate}
ORDER BY MaxMoney DESC LIMIT 0,1

SELECT SUM(product_price*product_quantity -coupon_amount-order_promotion_amount-promotion_amount-red_amount) FROM orderItem WHERE buyer_member_id=#{memberId} AND status='SUCCESS'
AND create_date >#{beginDate}
AND create_date <#{endDate}

mapper.update:
UPDATE orderItem
SET cash_amount=#{cashAmount},
gift_amount=#{giftAmount}
WHERE id=#{id}

UPDATE OrderItem SET modify_date
=now(),item_memo=#{itemMemo}
WHERE id =#{id}

UPDATE OrderItem
SET
modify_date=now(),comment_id=#{commentId}
WHERE id=#{id}

UPDATE OrderItem
SET delivery_bar_code=#{barcode}
WHERE id = #{id}

UPDATE OrderItem
SET cash_amount=#{cashAmount}, gift_amount=#{giftAmount}
WHERE id = #{id}

UPDATE OrderItem SET modify_date=now(), store_id=#{storeId}, store_memo=#{store_memo}, delivery_bar_code=#{deliveryBarCode}, bind_date=NOW()
,item_memo=#{itemMemo}
WHERE id=#{id} AND status='NORMAL'

UPDATE orderItem SET
compensation_amount=compensation_amount+#{compansation}
WHERE id=#{id}

mapper.select:
SELECT buyer_member_id AS 会员ID, buyer_member_name AS 会员账号, actualAmount AS 完结订单实付金额, awardId AS 中奖ID FROM (SELECT buyer_member_id, buyer_member_name, SUM(product_price*product_quantity-coupon_amount-red_amount -order_promotion_amount-promotion_amount-partner_amount) AS actualAmount FROM orderItem WHERE payment_time > #{beginPaymentDate} AND payment_time < #{endPaymentDate} AND `status`='SUCCESS' AND finish_date > #{beginFinishDate} AND finish_date < #{endFinishDate} AND buyer_member_id IN (SELECT DISTINCT member_id FROM o_award_record WHERE award_id IN
#{awardId}
) GROUP BY buyer_member_id)t1 INNER JOIN (SELECT member_id, MAX(award_id) AS awardId FROM o_award_record WHERE award_id IN
#{awardId}
GROUP BY member_id)t2 ON t1.buyer_member_id=t2.member_id;

SELECT COUNT(1) FROM
orderItem WHERE `status`='NORMAL'and refund_id is NULL and
estimate_date <=#{deadline}

SELECT * FROM orderItem
WHERE `status`='NORMAL'and refund_id is NULL and
estimate_date <=#{deadline}
LIMIT #{page.startNumber},#{page.pageSize}

SELECT * FROM orderItem 
WHERE product_source='CAOMEI' AND `status`='DELIVERED' AND delivery_sn LIKE 'SN%'
LIMIT #{page.startNumber},#{page.pageSize}

SELECT s.id, s. CODE, s. NAME, o.money FROM o_store s LEFT JOIN ( SELECT dplus_id, SUM(product_price*product_quantity-promotion_amount-order_promotion_amount-red_amount-coupon_amount) AS money FROM orderItem WHERE type = 'distribution' AND `status` != 'CLOSE' AND `status` != 'MALLCLOSE' AND dplus_id IS NOT NULL
AND create_date > #{startDate}
AND create_date < #{endDate}
GROUP BY dplus_id ) o ON s.id = o.dplus_id WHERE s.bus_type = 'DPLUS'

SELECT DATE_FORMAT(create_date,'%Y-%m-%d') AS day, dplus_id, dplus_code, dplus_name, SUM(product_price*product_quantity-promotion_amount-order_promotion_amount-red_amount-coupon_amount) AS money FROM orderItem WHERE type = 'distribution' AND `status` != 'CLOSE' AND `status` != 'MALLCLOSE' AND dplus_id=#{storeId}
AND create_date > #{startDate}
AND create_date < #{endDate}
GROUP BY day
