mapper.select:
SELECT count(*) FROM (SELECT orderitem.id FROM orderItem LEFT JOIN (SELECT p_brand.id,p_brand.operation AS operation FROM p_brand) d ON d.id=orderitem.designer_id WHERE 1=1 and `status` NOT IN('MALLCLOSE', 'CLOSE', 'INIT')
AND orderitem.create_date >=#{searcher.startDate}
AND orderitem.create_date <=#{searcher.endDate}
AND designer_name IN
#{name}
AND operation=#{searcher.operation}
GROUP BY designer_name)t

SELECT designer_code AS designerCode, designer_name AS name, SUM(product_price*product_quantity-orderitem.coupon_amount-orderitem.promotion_amount-order_promotion_amount-orderitem.red_amount) AS price, SUM(product_quantity) AS orderCount, designer_id AS designerId, d.`operation` AS operation FROM orderItem LEFT JOIN (SELECT p_brand.id,p_brand.operation AS operation FROM p_brand)d ON d.id=orderitem.designer_id WHERE 1=1 and `status` NOT IN('MALLCLOSE', 'CLOSE', 'INIT')
AND orderitem.create_date >=#{searcher.startDate}
AND orderitem.create_date <=#{searcher.endDate}
AND designer_name IN
#{name}
AND operation=#{searcher.operation}
GROUP BY designer_name ORDER BY price DESC LIMIT #{pager.startNumber},#{pager.pageSize}

SELECT IFNULL(SUM(pay_money),0) as amount,IFNULL(SUM(quantity),0) as count,YEAR(r.create_date) as year,MONTH(r.create_date) as month,orders.device as device FROM o_order_refund r LEFT JOIN orders ON r.order_id=orders.id where 1=1 AND order_pay_type!=3 and YEAR(r.create_date)=#{year} and MONTH(r.create_date)=#{month} and orders.device=#{device}
and r.reship_id is not null
and r.reship_id is null

SELECT COUNT(DISTINCT
member_id) FROM orders WHERE payment_time > #{beginDate} AND
payment_time < #{endDate}
